# dot completion                                           -*- shell-script -*-

# This bash completions script was generated by
# completely (https://github.com/dannyben/completely)
# Modifying it manually is not recommended

_dot_completions_filter() {
  local words="$1"
  local cur=${COMP_WORDS[COMP_CWORD]}
  local result=()

  if [[ "${cur:0:1}" == "-" ]]; then
    echo "$words"
  
  else
    for word in $words; do
      [[ "${word:0:1}" != "-" ]] && result+=("$word")
    done

    echo "${result[*]}"

  fi
}

_dot_completions() {
  local cur=${COMP_WORDS[COMP_CWORD]}
  local compwords=("${COMP_WORDS[@]:1:$COMP_CWORD-1}")
  local compline="${compwords[*]}"

  case "$compline" in
    'color-test'*)
      while read -r; do COMPREPLY+=( "$REPLY" ); done < <( compgen -W "$(_dot_completions_filter "--help -h")" -- "$cur" )
      ;;

    'update'*)
      while read -r; do COMPREPLY+=( "$REPLY" ); done < <( compgen -W "$(_dot_completions_filter "--help --yes -h -y")" -- "$cur" )
      ;;

    'edit'*)
      while read -r; do COMPREPLY+=( "$REPLY" ); done < <( compgen -W "$(_dot_completions_filter "--help -h")" -- "$cur" )
      ;;

    *)
      while read -r; do COMPREPLY+=( "$REPLY" ); done < <( compgen -W "$(_dot_completions_filter "--help --version -h -v color-test edit update")" -- "$cur" )
      ;;

  esac
} &&
complete -F _dot_completions dot

